class ChapterDetailModel {
  ChapterDetailModel({
    this.id,
    this.header,
    this.slug,
    this.body,
    this.viewCount,
    this.uploadDate,
    this.updatedDate,
    this.deletedAt,
    this.story,
  });

  ChapterDetailModel.fromJson(dynamic json) {
    id = json['id'];
    header = json['header'];
    slug = json['slug'];
    body = json['body'] != null ? json['body'].cast<String>() : [];
    viewCount = json['viewCount'];
    uploadDate = json['uploadDate'] != null ? DateTime.parse(json['uploadDate']).toUtc() : null;
    updatedDate = json['updatedDate'] != null ? DateTime.parse(json['updatedDate']).toUtc() : null;
    deletedAt = json['deletedAt'] != null ? DateTime.parse(json['deletedAt']).toUtc() : null;
    story = json['story'] != null ? StoryModel.fromJson(json['story']) : null;
  }
  int? id;
  String? header;
  String? slug;
  List<String>? body;
  int? viewCount;
  DateTime? uploadDate;
  DateTime? updatedDate;
  DateTime? deletedAt;
  StoryModel? story;

  Map<String, dynamic> toJson() {
    final map = <String, dynamic>{};
    map['id'] = id;
    map['header'] = header;
    map['slug'] = slug;
    map['body'] = body;
    map['viewCount'] = viewCount;
    map['uploadDate'] = uploadDate?.toUtc().toIso8601String();
    map['updatedDate'] = updatedDate?.toUtc().toIso8601String();
    map['deletedAt'] = deletedAt?.toUtc().toIso8601String();
    if (story != null) {
      map['story'] = story?.toJson();
    }
    return map;
  }
}
